//#include <stdio.h>

#define FPS 20 // 60

// tamanho do mapa, da tela e dos "quadradinhos"
#define TAM_TILE 60
#define LINHAS_MAPA 12
#define COLUNAS_MAPA 240
#define LINHAS_TELA 12
#define COLUNAS_TELA 30

